var File = function(file) {
	this.name = file.name;
	this.size = file.size;
	this.type = file.type;
}

File.prototype = {
	allowedStatuses: ['new', 'ready', 'sending', 'sent'],
	setStatus: function(newStatus) {
		this._status = newStatus;
	},
	getStatus: function() {
		return this._status;
	}
};

module.exports = File;
